@@ -1,1 +1,80 @@
-
+import React, { useState } from "react";
+import axios from "axios";
+
+export default function DecorApiPage() {
+  const [query, setQuery] = useState("");
+  const [results, setResults] = useState([]);
+  const [isLoading, setIsLoading] = useState(false);
+  const [error, setError] = useState(null);
+
+  const handleGenerate = async () => {
+    if (!query) {
+      setError("Please enter a keyword");
+      return;
+    }
+    setIsLoading(true);
+    setError(null);
+
+    try {
+      const response = await axios.post(
+        "https://api.decor8.ai/generateDesignsForRoom",
+        {
+          prompt: query,       // Ø¨Ø¯Ù„ input_image_url
+          room_type: "kidsroom",
+          design_style: "modern",
+          num_images: 1,
+          scale_factor: 2
+        },
+        {
+          headers: {
+            "Content-Type": "application/json",
+            "Authorization": "Bearer YOUR_DECOR8_API_KEY"
+          }
+        }
+      );
+      setResults(response.data.design_images || []);
+    } catch (err) {
+      console.error(err);
+      setError("Failed to generate design");
+    } finally {
+      setIsLoading(false);
+    }
+  };
+
+  return (
+    <div className="container my-5">
+      <h2 className="text-center mb-4">Decor Design with AI</h2>
+      <div className="mb-3">
+        <input
+          type="text"
+          className="form-control"
+          placeholder="Enter keyword (e.g., hd kids room)"
+          value={query}
+          onChange={(e) => setQuery(e.target.value)}
+        />
+        <button
+          onClick={handleGenerate}
+          className="btn btn-primary mt-2"
+          disabled={isLoading}
+        >
+          {isLoading ? "Generating..." : "Generate Design"}
+        </button>
+      </div>
+
+      {error && <p className="text-danger">{error}</p>}
+
+      <div className="row">
+        {results.map((imgUrl, index) => (
+          <div key={index} className="col-md-6 mb-3">
+            <img
+              src={imgUrl}
+              alt={`Design ${index}`}
+              className="w-100"
+              style={{ height: "300px", objectFit: "cover" }}
+            />
+          </div>
+        ))}
+      </div>
+    </div>
+  );
+}

