\ No newline at end of file
+  import React, { useState, useEffect } from 'react';
+import axios from 'axios';
+import { Link } from 'react-router-dom';
+
+export default function Accessories() {
+  const [products, setProducts] = useState([]);
+  const [isLoading, setIsLoading] = useState(true);
+  const [error, setError] = useState(null);
+  const [priceFilter, setPriceFilter] = useState("all");
+  const [sortOrder, setSortOrder] = useState("none");
+
+  const endpoint = 'https://fakestoreapi.com/products/category/jewelery';
+
+  function getProducts() {
+    setIsLoading(true);
+    setError(null);
+
+    axios.get(endpoint)
+      .then(response => {
+        if (response.data && Array.isArray(response.data)) {
+          setProducts(response.data);
+        } else {
+          setProducts([]);
+        }
+        setIsLoading(false);
+      })
+      .catch(error => {
+        console.error("Error fetching data:", error);
+        setError("Failed to fetch products. Please check your connection.");
+        setIsLoading(false);
+      });
+  }
+
+  useEffect(() => {
+    getProducts();
+  }, []);
+
+  let filteredProducts = products;
+
+  if (priceFilter === "low") {
+    filteredProducts = products.filter(p => p.price < 50);
+  }
+  if (priceFilter === "mid") {
+    filteredProducts = products.filter(p => p.price >= 50 && p.price <= 150);
+  }
+  if (priceFilter === "high") {
+    filteredProducts = products.filter(p => p.price > 150);
+  }
+
+  if (sortOrder === "asc") {
+    filteredProducts = [...filteredProducts].sort((a, b) => a.price - b.price);
+  }
+  if (sortOrder === "desc") {
+    filteredProducts = [...filteredProducts].sort((a, b) => b.price - a.price);
+  }
+
+  return (
+    <div className="container my-5">
+      <h2 className="text-center mb-4 fw-bold">Accessories</h2>
+
+      <div className="d-flex justify-content-end mb-4 gap-3">
+        <select
+          className="form-select w-auto"
+          value={priceFilter}
+          onChange={(e) => setPriceFilter(e.target.value)}
+        >
+          <option value="all">All Prices</option>
+          <option value="low">Under $50</option>
+          <option value="mid">$50 - $150</option>
+          <option value="high">Above $150</option>
+        </select>
+
+        <select
+          className="form-select w-auto"
+          value={sortOrder}
+          onChange={(e) => setSortOrder(e.target.value)}
+        >
+          <option value="none">Sort</option>
+          <option value="asc">Price: Low â†’ High</option>
+          <option value="desc">Price: High â†’ Low</option>
+        </select>
+      </div>
+
+      {isLoading && (
+        <div className="text-center my-5">
+          <div className="spinner-border text-primary"></div>
+          <p>Loading accessories...</p>
+        </div>
+      )}
+
+      {!isLoading && error && (
+        <div className="alert alert-danger text-center">{error}</div>
+      )}
+
+      {!isLoading && !error && filteredProducts.length === 0 && (
+        <div className="alert alert-info text-center">No products found.</div>
+      )}
+
+      {!isLoading && !error && filteredProducts.length > 0 && (
+        <div className="row">
+          {filteredProducts.map((product) => (
+            <div key={product.id} className="col-lg-3 col-md-4 col-sm-6 mb-4">
+              <div className="card shadow-sm h-100">
+                <img
+                  src={product.image}
+                  alt={product.title}
+                  className="w-100"
+                  style={{ height: "200px", objectFit: "contain" }}
+                />
+                <div className="card-body d-flex flex-column">
+                  <h5 className="card-title text-truncate">{product.title}</h5>
+                  <p className="text-muted small">Category: {product.category}</p>
+                  <p className="flex-grow-1 small">
+                    {product.description.split(" ").slice(0, 10).join(" ") + "..."}
+                  </p>
+                  <div className="d-flex justify-content-between align-items-center mt-auto">
+                    <span className="h5 text-success mb-0">${product.price}</span>
+                    <Link
+                      to={`/product/${product.id}`}
+                      className="btn btn-sm btn-outline-primary"
+                    >
+                      View
+                    </Link>
+                  </div>
+                </div>
+              </div>
+            </div>
+          ))}
+        </div>
+      )}
+    </div>
+  );
+}
