+import React, { useState, useEffect } from "react";
+import axios from "axios";
+
+export default function Furniture() {
+  const [products, setProducts] = useState([]);
+  const [loading, setLoading] = useState(true);
+  const [priceFilter, setPriceFilter] = useState("all");
+  const [sortOrder, setSortOrder] = useState("none");
+
+  useEffect(() => {
+    axios.get("https://dummyjson.com/products?limit=100")
+      .then(res => {
+        const filtered = res.data.products.filter(p => p.category === "furniture");
+        setProducts(filtered);
+      })
+      .catch(err => console.error(err))
+      .finally(() => setLoading(false));
+  }, []);
+
+  let displayed = [...products];
+
+  if (priceFilter === "low") displayed = displayed.filter(p => p.price < 50);
+  if (priceFilter === "mid") displayed = displayed.filter(p => p.price >= 50 && p.price <= 150);
+  if (priceFilter === "high") displayed = displayed.filter(p => p.price > 150);
+
+  if (sortOrder === "asc") displayed.sort((a, b) => a.price - b.price);
+  if (sortOrder === "desc") displayed.sort((a, b) => b.price - a.price);
+
+  if (loading) return <p>Loading Furniture...</p>;
+  if (displayed.length === 0) return <p>No Furniture products found.</p>;
+
+  return (
+    <div className="container my-4">
+      <h2 className="text-center mb-4">Furniture</h2>
+
+      <div className="d-flex justify-content-end mb-4 gap-3">
+        <select className="form-select w-auto" value={priceFilter} onChange={e => setPriceFilter(e.target.value)}>
+          <option value="all">All Prices</option>
+          <option value="low">Under $50</option>
+          <option value="mid">$50 - $150</option>
+          <option value="high">Above $150</option>
+        </select>
+
+        <select className="form-select w-auto" value={sortOrder} onChange={e => setSortOrder(e.target.value)}>
+          <option value="none">Sort</option>
+          <option value="asc">Price: Low â†’ High</option>
+          <option value="desc">Price: High â†’ Low</option>
+        </select>
+      </div>
+
+      <div className="row">
+        {displayed.map(product => (
+          <div key={product.id} className="col-md-4 mb-4">
+            <div className="card">
+              <img
+                src={product.images[0] || "https://via.placeholder.com/200"}
+                alt={product.title}
+                style={{ height: "200px", objectFit: "contain" }}
+                className="card-img-top"
+              />
+              <div className="card-body">
+                <h5 className="card-title">{product.title}</h5>
+                <p>${product.price}</p>
+                <p>{product.description}</p>
+              </div>
+            </div>
+          </div>
+        ))}
+      </div>
+    </div>
+  );
+}
