+import React, { useState, useEffect } from "react";
+import axios from "axios";
+import { Link } from "react-router-dom";
+
+export default function Kids() {
+  const [products, setProducts] = useState([]);
+  const [isLoading, setIsLoading] = useState(true);
+  const [error, setError] = useState(null);
+
+  const [priceFilter, setPriceFilter] = useState("all");
+  const [sortOrder, setSortOrder] = useState("none");
+
+  function getProducts() {
+    setIsLoading(true);
+    setError(null);
+
+    axios
+      .get("/kids.json") // File located inside public folder
+      .then((response) => {
+        setProducts(response.data);
+        setIsLoading(false);
+      })
+      .catch((error) => {
+        console.error("Error fetching kids products:", error);
+        setError("Failed to load kids products.");
+        setIsLoading(false);
+      });
+  }
+
+  useEffect(() => {
+    getProducts();
+  }, []);
+
+  // -------- Filter + Sort --------
+  let filteredProducts = products;
+
+  // Filter by price
+  if (priceFilter === "low") filteredProducts = products.filter(p => p.price < 20);
+  if (priceFilter === "mid") filteredProducts = products.filter(p => p.price >= 20 && p.price <= 35);
+  if (priceFilter === "high") filteredProducts = products.filter(p => p.price > 35);
+
+  // Sort by price
+  if (sortOrder === "asc")
+    filteredProducts = [...filteredProducts].sort((a, b) => a.price - b.price);
+
+  if (sortOrder === "desc")
+    filteredProducts = [...filteredProducts].sort((a, b) => b.price - a.price);
+
+  return (
+    <div className="container my-5">
+      <h2 className="text-center mb-4 fw-bold">Kids Products</h2>
+
+      {/* Filter + Sort */}
