+import React, { useState, useEffect } from "react";
+import axios from "axios";
+import { Link } from "react-router-dom";
+
+export default function Kids() {
+  const [products, setProducts] = useState([]);
+  const [isLoading, setIsLoading] = useState(true);
+  const [error, setError] = useState(null);
+
+  const [priceFilter, setPriceFilter] = useState("all");
+  const [sortOrder, setSortOrder] = useState("none");
+
+  function getProducts() {
+    setIsLoading(true);
+    setError(null);
+
+    axios
+      .get("/kids.json") // File located inside public folder
+      .then((response) => {
+        setProducts(response.data);
+        setIsLoading(false);
+      })
+      .catch((error) => {
+        console.error("Error fetching kids products:", error);
+        setError("Failed to load kids products.");
+        setIsLoading(false);
+      });
+  }
+
+  useEffect(() => {
+    getProducts();
+  }, []);
+
+  // -------- Filter + Sort --------
+  let filteredProducts = products;
+
+  // Filter by price
+  if (priceFilter === "low") filteredProducts = products.filter(p => p.price < 20);
+  if (priceFilter === "mid") filteredProducts = products.filter(p => p.price >= 20 && p.price <= 35);
+  if (priceFilter === "high") filteredProducts = products.filter(p => p.price > 35);
+
+  // Sort by price
+  if (sortOrder === "asc")
+    filteredProducts = [...filteredProducts].sort((a, b) => a.price - b.price);
+
+  if (sortOrder === "desc")
+    filteredProducts = [...filteredProducts].sort((a, b) => b.price - a.price);
+
+  return (
+    <div className="container my-5">
+      <h2 className="text-center mb-4 fw-bold">Kids Products</h2>
+
+      {/* Filter + Sort */}
+      <div className="d-flex justify-content-end mb-4 gap-3">
+        <select
+          className="form-select w-auto"
+          value={priceFilter}
+          onChange={(e) => setPriceFilter(e.target.value)}
+        >
+          <option value="all">All Prices</option>
+          <option value="low">Under $20</option>
+          <option value="mid">$20 - $35</option>
+          <option value="high">Above $35</option>
+        </select>
+
+        <select
+          className="form-select w-auto"
+          value={sortOrder}
+          onChange={(e) => setSortOrder(e.target.value)}
+        >
+          <option value="none">Sort</option>
+          <option value="asc">Price: Low â†’ High</option>
+          <option value="desc">Price: High â†’ Low</option>
+        </select>
+      </div>
+
+      {/* Loading */}
+      {isLoading && (
+        <div className="text-center my-5">
+          <div className="spinner-border text-primary"></div>
+          <p>Loading products...</p>
+        </div>
+      )}
+
+      {/* Error */}
+      {!isLoading && error && (
+        <div className="alert alert-danger text-center">{error}</div>
+      )}
+
+      {/* No products */}
+      {!isLoading && !error && filteredProducts.length === 0 && (
+        <div className="alert alert-info text-center">No products found.</div>
+      )}
+
+      {/* Products */}
+      {!isLoading && !error && filteredProducts.length > 0 && (
+        <div className="row">
+          {filteredProducts.map((product) => (
+            <div key={product.id} className="col-lg-3 col-md-4 col-sm-6 mb-4">
+
+              <div className="card shadow-sm h-100">
+
+                <img
+                  src={product.thumbnail}
+                  alt={product.title}
+                  className="w-100"
+                  style={{ height: "200px", objectFit: "cover" }}
+                />
+
+                <div className="card-body d-flex flex-column">
+                  <h5 className="card-title text-truncate">{product.title}</h5>
+
+                  <p className="text-muted small">Category: {product.category}</p>
+
+                  <p className="flex-grow-1 small">
+                    {product.description.split(" ").slice(0, 10).join(" ") + "..."}
+                  </p>
+
+                  <div className="d-flex justify-content-between align-items-center mt-auto">
+                    <span className="h5 text-success mb-0">${product.price}</span>
+
+                    <Link
+                      to={`/product/${product.id}`}
+                      className="btn btn-sm btn-outline-primary"
+                    >
+                      View
+                    </Link>
+                  </div>
+                </div>
+
+              </div>
+
+            </div>
+          ))}
+        </div>
+      )}
+    </div>
+  );
+}
