}

.amazonImgWrapper {
  width: 100%;
  height: 260px;
  display: flex;
  justify-content: center;
  align-items: center;
  background: #fff;
}

.amazonImg {
  max-height: 100%;
  width: 100%;
  object-fit: contain;
}

.amazonTitle {
  font-size: 14px;
  font-weight: 600;
  height: 40px;
  overflow: hidden;
  text-overflow: ellipsis;
}
