+ import React, { useContext } from 'react';
+import { useFormik } from 'formik';
+import { useNavigate } from 'react-router-dom';
+import * as Yup from 'yup';
+import styles from './Login.module.css';
+import { UserContext } from '../context/userContext';
+
+export default function Login() {
+  const navigate = useNavigate();
+  const { setLogin } = useContext(UserContext);
+
+  const handleLogin = (formData) => {
+    if (formData.email === 'KK78@gmail.com' && formData.password === 'Babc123') {
+      localStorage.setItem('userToken', 'admin-token');
+      setLogin(true);
+      navigate('/dashboard');
+    } else {
+      alert('Invalid email or password');
+      setLogin(false);
+    }
+  };
+
+  const validationSchema = Yup.object({
+    email: Yup.string().required('Required').email('Invalid email format'),
+    password: Yup.string().required('Required'),
+  });
+
+  const formik = useFormik({
+    initialValues: { email: '', password: '' },
+    validationSchema,
+    onSubmit: handleLogin,
+  });
+
+  return (
+    <div className={styles.authContainer}>
+      <div className={styles.authCard}>
+        <h2 className={styles.formHeader}>Login</h2>
+
+        <form onSubmit={formik.handleSubmit}>
+          <div className={styles.formGroup}>
+            <input
+              type="email"
+              name="email"
+              placeholder="Email"
+              value={formik.values.email}
+              onChange={formik.handleChange}
+              onBlur={formik.handleBlur}
+              className={formik.touched.email && formik.errors.email ? styles.inputError : ''}
+            />
+          </div>
+
+          <div className={styles.formGroup}>
+            <input
+              type="password"
+              name="password"
+              placeholder="Password"
+              value={formik.values.password}
+              onChange={formik.handleChange}
+              onBlur={formik.handleBlur}
+              className={formik.touched.password && formik.errors.password ? styles.inputError : ''}
+            />
+          </div>
+
+          <button type="submit" className={styles.submitBtn}>Login</button>
+        </form>
+      </div>
+    </div>
+  );
+}
