@@ -0,0 +1,36 @@
+.cartWrapper {
+  position: relative;
+}
+
+.cartIcon {
+  font-size: 22px;
+  color: white;
+}
+
+.badge {
+  position: absolute;
+  top: -6px;
+  right: -10px;
+  background-color: red;
+  color: white;
+  border-radius: 50%;
+  padding: 3px 7px;
+  font-size: 11px;
+  font-weight: bold;
+  animation: pop 0.3s ease-in-out;
+}
+
+/* أنيميشن */
+@keyframes pop {
+  0% {
+    transform: scale(0.5);
+    opacity: 0.5;
+  }
+  70% {
+    transform: scale(1.3);
+    opacity: 1;
+  }
+  100% {
+    transform: scale(1);
+  }
+}

