+import { useLocation, Link } from "react-router-dom";
+import { useContext, useState } from "react";
+import { CartContext } from "../context/CartContext";
+
+export default function ProductDetails() {
+  const { state } = useLocation();
+  const { addToCart } = useContext(CartContext);
+  const [added, setAdded] = useState(false);
+
+  const product = state?.product;
+
+  if (!product) {
+    return (
+      <div className="text-center mt-5">
+        <h2>No product data found</h2>
+        <Link to="/" className="btn btn-primary mt-3">
+          Go to Home
+        </Link>
+      </div>
+    );
+  }
+
+  const productImage =
+    product.images?.length > 0
+      ? product.images[0]
+      : product.thumbnail || "/placeholder.png";
+
+  const handleAddToCart = () => {
+    addToCart(product);
+    setAdded(true);
+    setTimeout(() => setAdded(false), 1500);
+  };
+
+  return (
+    <div className="container my-5">
+      {/* Breadcrumb */}
+      <nav aria-label="breadcrumb">
+        <ol className="breadcrumb">
+          <li className="breadcrumb-item">
+            <Link to="/">Home</Link>
+          </li>
+          <li className="breadcrumb-item">
+            <Link to={`/category/${product.category}`}>{product.category}</Link>
+          </li>
+          <li className="breadcrumb-item active" aria-current="page">
+            {product.title}
+          </li>
+        </ol>
+      </nav>
+
+      <div className="row align-items-center">
+        {/* صورة المنتج */}
+        <div className="col-12 col-md-5 mb-4 mb-md-0 d-flex justify-content-center">
+          <img
+            src={productImage}
+            alt={product.title}
+            className="img-fluid rounded shadow-sm"
+            style={{
+              maxHeight: "400px",
+              width: "100%",
+              objectFit: "cover",
+              border: "1px solid #ddd",
+              padding: "5px",
+              backgroundColor: "#f9f9f9",
+              transition: "transform 0.3s",
+              cursor: "pointer",
+            }}
+            onMouseOver={(e) => (e.currentTarget.style.transform = "scale(1.05)")}
+            onMouseOut={(e) => (e.currentTarget.style.transform = "scale(1)")}
+          />
+        </div>
+
+        {/* تفاصيل المنتج */}
+        <div className="col-12 col-md-7">
+          <h2 className="fw-bold">{product.title}</h2>
+          <p className="text-muted">{product.category}</p>
+          <p className="mt-3">{product.description}</p>
+          <h3 className="text-success fw-bold mt-4">${product.price}</h3>
+
+          <button
+            className="btn btn-success mt-4 w-100 w-md-auto"
+            onClick={handleAddToCart}
+            disabled={added}
+          >
+            {added ? "Added!" : "Add to Cart"}
+          </button>
+        </div>
+      </div>
+    </div>
+  );
+}

