+import "slick-carousel/slick/slick-theme.css";
+
+export default function ProductsSlider() {
+  const [products, setProducts] = useState([]);
+  const [selectedCategory, setSelectedCategory] = useState("handmade");
+  const [isLoading, setIsLoading] = useState(true);
+  const [error, setError] = useState(null);
+  const [priceFilter, setPriceFilter] = useState("all");
+  const [sortOrder, setSortOrder] = useState("none");
+
+  const categoryFiles = {
+    handmade: "/Handmade.json",
+    kids: "/Kids-products.json",
+    men: "/Men.json",
+    women: "/Women.json"
+  };
+
+  const getProducts = (category) => {
+    setIsLoading(true);
+    setError(null);
+    axios.get(categoryFiles[category])
+      .then(res => {
+        setProducts(res.data);
+        setIsLoading(false);
+      })
+      .catch(err => {
+        setError("Failed to load products.");
+        setIsLoading(false);
+      });
+  };
+
+  useEffect(() => {
+    getProducts(selectedCategory);
+  }, [selectedCategory]);
+
+  let filteredProducts = products;
+  if (priceFilter === "low") filteredProducts = filteredProducts.filter(p => p.price < 20);
