+  if (priceFilter === "high") filteredProducts = filteredProducts.filter(p => p.price > 35);
+  if (sortOrder === "asc") filteredProducts = [...filteredProducts].sort((a,b)=>a.price-b.price);
+  if (sortOrder === "desc") filteredProducts = [...filteredProducts].sort((a,b)=>b.price-a.price);
+
+  const sliderSettings = {
+    dots: true,
+    infinite: true,
+    speed: 500,
+    slidesToShow: 3,
+    slidesToScroll: 1,
+    arrows: true,
+    autoplay: true,
+    autoplaySpeed: 2500,
+    responsive: [
+      { breakpoint: 1024, settings: { slidesToShow: 2 } },
+      { breakpoint: 768, settings: { slidesToShow: 1 } },
+    ],
+  };
+
+  if (isLoading) return <p>Loading...</p>;
+  if (error) return <p>{error}</p>;
+
+  return (
+    <div className="container my-5">
+      <div className="d-flex justify-content-between mb-3 gap-3">
+        <select className="form-select w-auto" value={selectedCategory} onChange={e=>setSelectedCategory(e.target.value)}>
+          <option value="handmade">Handmade</option>
+          <option value="kids">Kids</option>
+          <option value="men">Men</option>
+          <option value="women">Women</option>
+        </select>
+
+        <select className="form-select w-auto" value={priceFilter} onChange={e=>setPriceFilter(e.target.value)}>
+          <option value="all">All Prices</option>
+          <option value="low">Under $20</option>
+          <option value="mid">$20 - $35</option>
+          <option value="high">Above $35</option>
+        </select>
+
+        <select className="form-select w-auto" value={sortOrder} onChange={e=>setSortOrder(e.target.value)}>
+          <option value="none">Sort</option>
+          <option value="asc">Price: Low → High</option>
+          <option value="desc">Price: High → Low</option>
+        </select>
+      </div>
+
+      <Slider {...sliderSettings} key={filteredProducts.length}>
+        {filteredProducts.map(product => (
+          <div key={product.id} className="p-2">
+            <div className="card shadow-sm h-100">
