+import React, { useState, useEffect } from "react";
+import axios from "axios";
+import Slider from "react-slick";
+import { Link } from "react-router-dom";
+import "slick-carousel/slick/slick.css";
+import "slick-carousel/slick/slick-theme.css";
 
+export default function ProductsSliderح() {
+  const [handmade, setHandmade] = useState([]);
+  const [kids, setKids] = useState([]);
+  const [men, setMen] = useState([]);
+  const [women, setWomen] = useState([]);
+  const [isLoading, setIsLoading] = useState(true);
+  const [error, setError] = useState(null);
+
+  useEffect(() => {
+    const fetchProducts = async () => {
+      try {
+        const [handmadeRes, kidsRes, menRes, womenRes] = await Promise.all([
+          axios.get("/Handmade.json"),
+          axios.get("/Kids-products.json"),
+          axios.get("/Men.json"),
+          axios.get("/Women.json"),
+        ]);
+        setHandmade(handmadeRes.data);
+        setKids(kidsRes.data);
+        setMen(menRes.data);
+        setWomen(womenRes.data);
+      } catch (err) {
+        setError("Failed to load products.");
+      } finally {
+        setIsLoading(false);
+      }
+    };
+    fetchProducts();
+  }, []);
+
+  const sliderSettings = {
+    dots: true,
+    infinite: true,
+    speed: 500,
+    slidesToShow: 3,
+    slidesToScroll: 1,
+    arrows: true,
+    autoplay: true,
+    autoplaySpeed: 2500,
+    responsive: [
+      { breakpoint: 1024, settings: { slidesToShow: 2 } },
+      { breakpoint: 768, settings: { slidesToShow: 1 } },
+    ],
+  };
+
+  const renderSlider = (products, title) => (
+    <div className="my-5">
+      <h2 className="text-center mb-3">{title}</h2>
+      <Slider {...sliderSettings}>
+        {products.map((p) => (
+          <div key={p.id} className="p-2">
+            <div className="card shadow-sm h-100">
+              <img
+                src={p.thumbnail || p.image}
+                alt={p.title || p.name}
+                style={{ width: "100%", height: "250px", objectFit: "cover" }}
+              />
+              <div className="card-body text-center">
+                <h5 className="card-title text-truncate">{p.title || p.name}</h5>
+                <p className="text-success h6">${p.price}</p>
+                <Link
+                  to={`/product/${p.id}`}
+                  state={{ product: p }}
+                  className="btn btn-sm btn-outline-success mt-2"
+                >
+                  View Product
+                </Link>
+              </div>
+            </div>
+          </div>
+        ))}
+      </Slider>
+    </div>
+  );
+
+  if (isLoading)
+    return (
+      <div className="text-center my-5">
+        <div className="spinner-border text-primary"></div>
+        <p>Loading products...</p>
+      </div>
+    );
+
+  if (error) return <div className="alert alert-danger text-center">{error}</div>;
+
+  return (
+    <div className="container">
+      {renderSlider(handmade, "Handmade")}
+      {renderSlider(kids, "Kids")}
+      {renderSlider(men, "Men")}
+      {renderSlider(women, "Women")}
+    </div>
+  );
 }
+

