+                <Link to={`/product/${product.id}`} className="btn btn-sm btn-outline-primary">View</Link>
+              </div>
+            </div>
+          </div>
+        </div>
+      ))}
+    </div>
+  )}
+
+  <style>{`.card:hover .product-img {transform: scale(1.05);}`}</style>
+</div>
+```
+
+)
+}

