+import React, { useState, useEffect } from "react";
+import axios from "axios";
+import { Link } from "react-router-dom";
+import styles from "./Women.module.css";
+
+export default function Women() {
+  const [products, setProducts] = useState([]);
+  const [isLoading, setIsLoading] = useState(true);
+  const [error, setError] = useState(null);
+  const [priceFilter, setPriceFilter] = useState("all");
+  const [sortOrder, setSortOrder] = useState("none");
+
+  const getProducts = async () => {
+    setIsLoading(true);
+    setError(null);
+    try {
+      const res = await axios.get("/Women.json");
+      setProducts(res.data || []);
+    } catch (err) {
+      console.error(err);
+      setError("Failed to load women products.");
+    } finally {
+      setIsLoading(false);
+    }
+  };
+
+  useEffect(() => {
+    getProducts();
+  }, []);
+
+  let filteredProducts = products;
+
+  if (priceFilter === "low")
+    filteredProducts = products.filter((p) => p.price < 20);
+  if (priceFilter === "mid")
+    filteredProducts = products.filter((p) => p.price >= 20 && p.price <= 35);
+  if (priceFilter === "high")
+    filteredProducts = products.filter((p) => p.price > 35);
+
+  if (sortOrder === "asc")
+    filteredProducts = [...filteredProducts].sort((a, b) => a.price - b.price);
+  if (sortOrder === "desc")
+    filteredProducts = [...filteredProducts].sort((a, b) => b.price - a.price);
+
+  return (
+    <div className="container my-5">
+      <h2 className="text-center mt-4 fw-bold">Women Products</h2>
+
+      <div className="d-flex justify-content-end mb-4 gap-3">
+        <select
+          className="form-select w-auto"
+          value={priceFilter}
+          onChange={(e) => setPriceFilter(e.target.value)}
+        >
+          <option value="all">All Prices</option>
+          <option value="low">Under $20</option>
+          <option value="mid">$20 - $35</option>
+          <option value="high">Above $35</option>
+        </select>
+
+        <select
+          className="form-select w-auto"
+          value={sortOrder}
+          onChange={(e) => setSortOrder(e.target.value)}
+        >
+          <option value="none">Sort</option>
+          <option value="asc">Price: Low → High</option>
+          <option value="desc">Price: High → Low</option>
+        </select>
+      </div>
+
+      {isLoading && (
+        <div className="text-center my-5">
+          <div className="spinner-border text-primary"></div>
+          <p>Loading products...</p>
+        </div>
+      )}
+
+      {!isLoading && error && (
+        <div className="alert alert-danger text-center">{error}</div>
+      )}
+
+      {!isLoading && !error && filteredProducts.length === 0 && (
+        <div className="alert alert-info text-center">No products found.</div>
+      )}
+
+      {!isLoading && !error && filteredProducts.length > 0 && (
+        <div className="row g-4">
+          {filteredProducts.map((product) => (
+            <div key={product.id} className="col-lg-3 col-md-4 col-sm-6 d-flex">
+              <div className={styles.cardWrapper}>
+                <img
+                  src={product.thumbnail || "/fallback.jpg"}
+                  alt={product.title || "Product"}
+                  className={styles.cardImage}
+                />
+                <div className={styles.cardBody}>
+                  <h5 className={styles.cardTitle}>{product.title || "No title"}</h5>
+                  <p className="text-muted small">
+                    Category: {product.category || "N/A"}
+                  </p>
+                  <p className={styles.cardDescription}>
+                    {product.description?.split(" ").slice(0, 10).join(" ") ||
+                      "No description available."}
+                  </p>
+                  <div className={styles.cardFooter}>
+                    <span className="h5 text-success mb-0">
+                      ${product.price ?? "N/A"}
+                    </span>
+                    <Link
+                      to={`/product/${product.id}`}
+                      state={{ product }}
+                      className={`btn btn-sm btn-outline-success ${styles.btnView}`}
+                    >
+                      View
+                    </Link>
+                  </div>
+                </div>
+              </div>
+            </div>
+          ))}
+        </div>
+      )}
+    </div>
+  );
+}
